import "./style.css"
import { useParams } from 'react-router-dom';


export const Card = (data) => {
    const { id } = useParams();

    return (
        <div className="contactDiv">
            <div>
            {
            data
              .filter((list) => list.id === name)
              .map((list) => (
                <div className="full-card" key={ list.id }>
                  <h2>Name: {list.name}</h2>
                  <h4>Category: {list.category}</h4>
                </div>
              ))}
            </div>

        </div>
    )
}
